import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../../../context/CartContext';
import CartDetail from './CartDetail';
import s from './Cart.module.css';

const Cart = () => {
    const { cart, clearCart, deleteOne, totalPrice, disminuir, aumentar } =
        useContext(CartContext);
    //console.log(cart);

    if (cart.length === 0) {
        return (
            <div className={s.cartContainer}>
                <h1>
                    No contas con ningun producto agregado,{' '}
                    <Link to="/">Ac√°</Link> por aca te dejamos donde podes encontrar nuestros productos
                </h1>
            </div>
        );
    }
    return (
        <>
            <div className={s.cartContainer}>
                {cart.map((prod) => (
                    <CartDetail
                        key={prod.id}
                        prod={prod}
                        deleteOne={deleteOne}
                        disminuir={disminuir}
                        aumentar={aumentar}
                    />
                ))}
                <div>
                    <button className={s.vaciar} onClick={clearCart}>
                        Vaciar carrito
                    </button>
                    <h2>Total: ${totalPrice}</h2>
                </div>
            </div>
        </>
    );
};

export default Cart;
